CREATE TABLE `cities_tb` (                                                                                                                                                                                                                                                                                            
    `cityID` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,                                                                                                                                                                                                                                                             
    `cityName` varchar(50) NOT NULL,                                                                                                                                                                                                                                                                                    
    `countryCode` varchar(6) NOT NULL DEFAULT '',                                                                                                                                                                                                                                                                       
    PRIMARY KEY (`cityID`),                                                                                                                                                                                                                                                                                             
    UNIQUE KEY `cities_tb_uk_1` (`countryCode`,`cityID`)                                                                                                                                                                                                                                                                
);