CREATE TABLE `portfolio_performance_tb` 
(                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
    `portfolio_performance_id` int(11) NOT NULL AUTO_INCREMENT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
    `portfolio_id` int(10) unsigned NOT NULL,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
    `datetime` datetime DEFAULT NULL,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    `closing_value` decimal(12,2) DEFAULT '0.00',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
    `market_value` decimal(12,2) DEFAULT '0.00',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
    `market_value_after_cash_flow` decimal(12,2) DEFAULT '0.00' COMMENT 'Cash flow will be zero for all times.',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
    `sub_period_return` decimal(12,2) DEFAULT '0.00',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    PRIMARY KEY (`portfolio_performance_id`),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
    KEY `portfolio_performance_tb_fk_1` (`portfolio_id`),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
    CONSTRAINT `portfolio_performance_tb_fk_1` FOREIGN KEY (`portfolio_id`) REFERENCES `portfolio_tb` (`portfolio_id`)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
);
